import React from 'react';
import { IonList, IonItem, IonIcon, IonLabel, IonToggle } from '@ionic/react';
import { moon } from 'ionicons/icons';

const toggleLightModeHandler = () => {
    document.body.classList.toggle("light");
}

const LightModeToggle: React.FC = () => {
    return (
        <IonList className="ion-margin-top">
            <IonItem>
                <IonIcon slot="start" icon={moon}></IonIcon>
                <IonLabel>Light Mode</IonLabel>
                <IonToggle
                    slot="end"
                    name="lightMode"
                    onIonChange={toggleLightModeHandler}
                />
            </IonItem>
        </IonList>
    )
}

export default LightModeToggle;
